import React, { Component } from 'react'
import Navbar from "../components/Navbar"

import PeopleFeature from "./PeopleFeature"
import PartnerFeature from "./PartnerFeature"
import TripStyleFeature from "./TripStyleFeature"

class Quiz extends Component {
  constructor(props) {
    super(props)
    this.state = {
      currentPage: 0
    }
  }

  features = [
    <PeopleFeature/>,
    <PartnerFeature/>,
    <TripStyleFeature/>
  ]

  showNext = () => {
    this.setState({
      currentPage: this.state.currentPage+1
    })
  }

  showPrevious = () => {
    this.setState({
      currentPage: this.state.currentPage-1
    })
  }


  render() {
    return (
      <div className="quiz-container">
        <Navbar active="quiz"/>
        <div className="container">
          {this.features[this.state.currentPage]}
        </div>
        <div className="bottom-nav text-white">
          <span className={this.state.currentPage == 0 && "invisible"}onClick={this.showPrevious}>
            <img src="svgs/arrow_left.svg"/>
            Back
          </span>
          <span className={this.state.currentPage < this.features.length -1 && "invisible"} onClick={this.showNext}>
              Next
              <img src="svgs/arrow_right.svg"/>
          </span>
        </div>
      </div>
    )
  }
}

export default Quiz;
