import React, { Component } from 'react'
import RoomCard from "./RoomCard"
import DiningCard from "./DiningCard"
import ActivityCard from "./ActivityCard"

class CardList extends Component{
  constructor(props){
    super(props)
    this.state = {
      likedRooms: [],
      likedActivities: [],
      likedDining:[]
    }
  }
    createList = () => {
      return (this.props.list != undefined) && this.props.list.map( item => {
        let type = this.props.type
        switch (type) {
          case "room":
            return <RoomCard
                      room={item}
                      isLiked={this.state.likedRooms.indexOf(item) > -1}
                      onStar={this.handleDataChange}
                      dataKey="likedRooms"/>
          case "activity":
            return <ActivityCard
                      activity={item}
                      isLiked={this.state.likedActivities.indexOf(item) > -1}
                      onStar={this.handleDataChange}
                      dataKey="likedActivities"/>
          case "dining":
            return <DiningCard
                      dining={item}
                      isLiked={this.state.likedDining.indexOf(item) > -1}
                      onStar={this.handleDataChange}
                      dataKey="likedDining"/>
        }
      })
    }
    render() {
        let list = this.createList()
        return (
            <div className="card-list">
              <span id="top-recommendation"> Top Recommendation </span>
              {list[0]}
              <span id="other-recommendation"> Other Recommendations </span>
              {list.slice(1, list.length)}
            </div>
        )
    }
}

export default CardList
