import React, { Component } from 'react'

class Button extends React.Component{
    constructor(props) {
        super(props)
        this.state = {
            isClicked: false,
        }
    }

    click = () => {
      console.log(this)
        const newState = !this.state.isClicked
        this.setState({
            isClicked: newState,
        })
    }

    render() {
        const width: React.CSSProperties = {
            height: "48px",
            minWidth: this.props.width
        }
        return (
            <div
                className={this.state.isClicked ? "btn active" : "btn"}
                onClick={this.click}
                style={width}
            >
                {this.props.buttonText}
            </div>
        )
    }
}

export default Button
