import React, { Component } from 'react'

class Counter extends Component{
    constructor(props) {
        super(props)
        this.state = {
            data: 0,
        }
    }

    increment = () => {
        const newValue = this.state.data + 1
        if (newValue <= 6) this.changeValue(newValue)
    }

    decrement = () => {
        const newValue = this.state.data - 1
        if (newValue >= 0) this.changeValue(newValue)
    }

    changeValue = newValue => {
        this.setState({
            data: newValue,
        })
    }

    render() {
        return (
            <div className="counter-input">
                <div onClick={this.decrement}> <img src="svgs/minus_button.svg"/> </div>
                <span>{this.state.data}</span>
                <div onClick={this.increment}> <img src="svgs/plus_button.svg"/> </div>
            </div>
        )
    }
}

export default Counter;
