import React, { Component } from 'react'
import { Route, BrowserRouter as Router } from 'react-router-dom'

import Home from './Home'
import Quiz from './quiz/Quiz'
import Results from './results/Results'
import Loading from "./Loading"

import * as Db from "./data/Database"

class App extends Component {
  constructor(props){
    super(props)
    this.state = {
      data: {},
      rooms: [],
      activities: [],
      dining: [],
      tags: []
    }
  }

  handleDataChange = (updatedData) => {
    this.setState({
      data: updatedData
    }, this.getRecommendations())
  }

  getRecommendations = () => {
    let purpose = this.state.data.tripPurpose
    let styles = this.state.data.tripStyles
    console.log("Purpose: " + purpose)
    console.log("Styles: " + this.state.data.tripStyles)
    if(purpose == undefined || styles == undefined || styles.length == 0)
      return

    let rooms = []
    let activities = []
    let dining = []

    this.setTags(purpose, styles)

    for(let style of styles){
      if(style in Db.recommendation[purpose]){
        rooms = rooms.concat(Db.recommendation[purpose][style]["rooms"])
        activities = activities.concat(Db.recommendation[purpose][style]["activities"])
        dining = dining.concat(Db.recommendation[purpose][style]["dining"])
      }
    }

    this.setState({
      rooms: rooms,
      activities: activities,
      dining: dining
    })
  }

  setTags = (purpose, styles) => {
    let tags = ["Aug 1 - Aug 3"]
    tags.push(purpose)
    tags = tags.concat(styles)
    tags.push("Price")

    this.setState({
      tags: tags
    })
  }

  render() {
    return (
      <Router basename="/woodlanders_react_app">
        <Route path="/"component={Home} />
        <Route
          path='/quiz'
          render = {props => <Quiz {...props} onDataChange={this.handleDataChange}/>} />
        <Route path='/loading' component={Loading} />
        <Route
          path='/results'
          render= {props => <Results {...props}
                                tags={this.state.tags}
                                rooms={this.state.rooms}
                                activities={this.state.activities}
                                dining={this.state.dining}/>} />
      </Router>
    )
  }
}

export default App;
