import React, { Component } from 'react'
import RoomCard from "./RoomCard"
import DiningCard from "./DiningCard"
import ActivityCard from "./ActivityCard"

class CardList extends Component{
  constructor(props){
    super(props)
    this.state = {
      likedRooms: [],
      likedActivities: [],
      likedDining:[]
    }
  }

  handleDataChange = (key, item, isStarred) => {
    let list = this.state[key]
    if(isStarred)
      list.push(item)
    else
      list = list.filter(x => x != item)

    let newState = {}
    newState[key] = list
    this.setState(newState)
  }

  keyList = []

  createList = () => {
    this.keyList = [...new Set(this.props.list)]
    return (this.keyList != undefined) && this.keyList.map( item => {
      let type = this.props.type
      switch (type) {
        case "room":
          return <RoomCard
                    room={item}
                    key={item}
                    isLiked={this.state.likedRooms.indexOf(item) > -1}
                    onStar={this.handleDataChange}
                    dataKey="likedRooms"/>
        case "activity":
          return <ActivityCard
                    activity={item}
                    key={item}
                    isLiked={this.state.likedActivities.indexOf(item) > -1}
                    onStar={this.handleDataChange}
                    dataKey="likedActivities"/>
        case "dining":
          return <DiningCard
                    dining={item}
                    key={item}
                    isLiked={this.state.likedDining.indexOf(item) > -1}
                    onStar={this.handleDataChange}
                    dataKey="likedDining"/>
      }
    })
  }
  render() {
      let list = this.createList()

      if(list == undefined || list.length <= 0) return;
      return (
          <div className="card-list">
            <span id="top-recommendation"> Top Recommendation </span>
            {list[0]}
            <span id="other-recommendation"> Other Recommendations </span>
            {list.slice(1, list.length)}
          </div>
      )
  }
}

export default CardList
