import React, { Component } from 'react'
import { Route, BrowserRouter as Router } from 'react-router-dom'

import Home from './Home'
import Quiz from './quiz/Quiz'
import Results from './results/Results'
import Loading from "./Loading"

import * as Db from "./data/Database"

class App extends Component {
  constructor(props){
    super(props)
    this.state = {
      data: {},
      rooms: [],
      activities: [],
      dining: [],
    }
  }

  handleDataChange = (updatedData) => {
    this.setState({
      data: updatedData
    }, this.getRecommendations())
  }

  getRecommendations = () => {
    let purpose = this.state.data.tripPurpose
    let styles = this.state.data.tripStyles
    if(purpose == undefined || styles == undefined || styles.length == 0)
      return

    let rooms = []
    let activities = []
    let dining = []

    for(let style of styles){
      rooms.concat(Db.recommendation[purpose][style]["room"])
      activities.concat(Db.recommendation[purpose][style]["activities"])
      dining.concat(Db.recommendation[purpose][style]["dining"])
    }

    console.log("Purpose: " + purpose)
    console.log("Styles: " + this.state.data.tripStyles)
    console.log(rooms);
    console.log(activities);
    console.log(dining);
  }

  render() {
    return (
      <Router>
        <Route exact path='/' component={Home} />
        <Route
          path='/quiz'
          render = {props => <Quiz {...props} onDataChange={this.handleDataChange}/>} />
        <Route path='/loading' component={Loading} />
        <Route
          path='/results'
          render= {props => <Results {...props} data={this.state.data}/>} />
      </Router>
    )
  }
}

export default App;
