  constructor(props){
    super(props)
    this.state = {
      data: {},
      rooms: [],
      activities: [],
      dining: [],
    }
  }

  handleDataChange = (updatedData) => {
    this.setState({
      data: updatedData
    }, this.getRecommendations())
  }

