import React, { Component } from 'react'
import RoomCard from "./RoomCard"

class CardList extends Component{
    createList = () => {
      let type = this.props.type
      return this.props.list.map( (item, type) => {
        switch (type) {
          case "room":
            return <RoomCard room={item}/>
          case "activity":
            return <ActivityCard activity={item}/>
          case "dining":
            return <DiningCard dining={item}/>
        }
      })
    }
    render() {
        let list = this.createList()
