import React, { Component } from 'react'
import Navbar from "../components/Navbar"

import FeatureContainer from "./FeatureContainer"

class Quiz extends Component {
  constructor(props) {
    super(props)
    this.state = {
      currentPage: 0,
      adults: 0,
      children: 0,
      company: [],
      tripStyles: [],
      discounts: [],
      tripPurpose: "solo"
    }

  }

  features = undefined

  handleDataChange = (key, value) => {
    let state = {}
    state[key] = value
    this.setState(state, this.getTripPurpose)
  }

  getTripPurpose = () => {
    let purpose = this.state.purpose
    switch(this.state.adults){
      case 1:
        purpose = "solo"
        break;
      case 2:
        purpose = this.state.children > 0? "family" : this.checkCompany()
        break;
      default:
        purpose = this.state.children > 0? "family" : "friends"
    }
    this.setState({
      tripPurpose: purpose
    })
  }

  checkCompany = () => {
    company = this.state.company[0]
    if(company == "My Partner")
      return "couple"
    else if(company == "My Friend")
      return "friends"
    else
      return "family"
  }

  goToPage = page => {
    this.setState({
      currentPage: page
    })
  }

  hideNext = () => {
    if(this.state.currentPage >= this.features.length -1 ||
    this.state.currentPage == 1)
      return "invisible"
  }

  hidePrevious = () => {
    if(this.state.currentPage == 0)
      return "invisible"
  }


  render() {
    this.features = [
      <FeatureContainer
        title="How many people are in your party?"
        counters={true}
        onDataChange = {this.handleDataChange}
        adults = {this.state["adults"]}
        children = {this.state["children"]}/>,
      <FeatureContainer
        title="Who is joining you?"
        buttonList={["My Partner", "My Friend", "Other"]}
        radio={true}
        onButtonsClicked={this.handleDataChange}
        dataKey="company"
        data = {this.state["company"]}/>,
      <FeatureContainer
        title="What style of getaway are you looking for?"
        subtitle="Select up to 2"
        buttonList={["Adventure", "Romance", "Something"]}
        onButtonsClicked= {this.handleDataChange}
        dataKey = "tripStyles"
        data = {this.state["tripStyles"]}/>,
      <FeatureContainer
        title="Are you a member or customer of any of the organizations below?"
        subtitle="Select as many as apply to maximize your discounts"
        buttonList={["AAA", "AARP", "U.S. Military or Veterans Association", "American Express"]}
        onButtonsClicked = {this.handleDataChange}
        dataKey = "discounts"
        data = {this.state["discounts"]}/>,
      <FeatureContainer
        title="What dates are you thinking of?"
        subtitle="We can give you better price estimates with dates!"
        calendar={true}/>
    ]

    return (
      <div className="quiz-container">
        <Navbar active="quiz"/>
        {this.features[this.state.currentPage]}
        <div className="bottom-nav text-white">
          <span className={this.hidePrevious} onClick={this.goToNext}>
            <img src="svgs/arrow_left.svg"/>
            Back
          </span>
          <span className={this.hideNext} onClick={this.goToPrevious}>
              Next
              <img src="svgs/arrow_right.svg"/>
          </span>
        </div>
      </div>
    )
  }
}

export default Quiz;
