import React, { Component } from 'react'
import Button from "./Button"

class ButtonList extends React.Component{
  constructor(props){
    super(props)
    this.state = {
      activeButtons: []
    }
  }

  onButtonClick= (button) => {
    let active = this.state.activeButtons;
    if(button.isActive){
      active = active.push(button.id)
    } else {
      active = active.filter(x => x != button.id)
    }
    this.setState({
      activeButtons: active
    })
  }

  getList = () => {
    console.log(this.state.activeButtons)
      return this.props.list.map((item)=>
         <Button
            key= {item}
            buttonText={item}
            width="130px" //TODO: Make sure this is not hardcoded
            isActive = {this.state.activeButtons.indexOf(item) > -1}
            onButtonClick = {this.onButtonClick}
        ></Button>
      )
  }

  render() {
      const width: React.CSSProperties = {
          minWidth: this.props.width,
      }
      return (
          <div className="btn-list" style={width}>
              {this.getList()}
          </div>
      )
  }
}

export default ButtonList
