import React, { Component } from 'react'

class Counter extends Component{
    increment = () => {
        const newValue = this.props.data + 1
        if (newValue <= 6) this.changeValue(newValue)
    }

    decrement = () => {
        const newValue = this.props.data - 1
        if (newValue >= 0) this.changeValue(newValue)
    }

    changeValue = newValue => {
      console.log(this.props.onDataChange)
        this.props.onDataChange(this.props.dataKey, newValue)
    }

    render() {
        return (
            <div className="counter-input">
                <div onClick={this.decrement}> <img src="svgs/minus_button.svg"/> </div>
                <span>{this.props.data}</span>
                <div onClick={this.increment}> <img src="svgs/plus_button.svg"/> </div>
            </div>
        )
    }
}

export default Counter;
