import React, { Component } from 'react'
import Button from "./Button"

class ButtonList extends React.Component{

    partner_list = ["My Partner", "My Friend", "Other"]

    getList = () => {
        let list = this.props.list == undefined?
        this.partner_list : this.props.list;

        return list.map((item)=> 
           <Button
              key= {item}
              buttonText={item}
              width="130px" //TODO: Make sure this is not hardcoded
          ></Button>
        )
    }

    render() {
        const width: React.CSSProperties = {
            minWidth: this.props.width,
        }
        return (
            <div
                className={
                    this.props.list_type == "trip_style"
                        ? "btn-list"
                        : "btn-list radio"
                }
                style={width}
            >
                {this.getList()}
            </div>
        )
    }
}

export default ButtonList
