import React, { Component } from 'react'
import ButtonList from "../components/ButtonList"
import Counter from "../components/Counter"
import Calendar from "../components/Calendar"

class FeatureContainer extends Component {

  counters = () => {
    return (
      <div>
        <div className="counter-container">
          <span>Adults</span>
          <Counter
            onDataChange={this.props.handleDataChange}
            dataKey="adults"
            data={this.props.adults}/>
        </div>
        <div className="counter-container">
          <div>
            <p>Kids</p>
            <p className="light-subtext">Ages 12 & under</p>
          </div>
          <Counter
            onDataChange={this.props.handleDataChange}
            dataKey="children"
            data={this.props.children}/>
        </div>
      </div>
    )
  }

  render() {
    return (
      <div className="flex-container">
        <div className="question-container">
          <span>{this.props.title}</span>
          <p className="subtext">{this.props.subtitle}</p>
        </div>
        <div className="interaction-container">
          {this.props.buttonList &&
            <ButtonList
              list={this.props.buttonList}
              radio={this.props.radio}
              onButtonsClicked = {this.props.onButtonsClicked}
              dataKey = {this.props.dataKey}
              data = {this.props.data}/>}
          {this.props.counters && this.counters()}
          {this.props.calendar && <Calendar/>}
        </div>
      </div>
    )
  }
}

export default FeatureContainer;
